let restaurant;
var map;

/**
 * Initialize Google map, called from HTML.
 */
window.initMap = () => {
  fetchRestaurantFromURL((error, restaurant) => {
    if (error) { // Got an error!
      console.error(error);
    } else {
      self.map = new google.maps.Map(document.getElementById('map'), {
        zoom: 16,
        center: restaurant.latlng,
        scrollwheel: false
      });
      fillBreadcrumb();
      DBHelper.mapMarkerForRestaurant(self.restaurant, self.map);
    }
  });
}

/**
 * Get current restaurant from page URL.
 */
fetchRestaurantFromURL = (callback) => {
  if (self.restaurant) { // restaurant already fetched!
    callback(null, self.restaurant)
    return;
  }
  const id = getParameterByName('id');
  if (!id) { // no id found in URL
    error = 'No restaurant id in URL'
    callback(error, null);
  } else {
    DBHelper.fetchRestaurantById(id, (error, restaurant) => {
      self.restaurant = restaurant;
      if (!restaurant) {
        console.error(error);
        return;
      }
      fillRestaurantHTML();
      callback(null, restaurant)
    });
  }
}

/**
 * Create restaurant HTML and add it to the webpage
 */
fillRestaurantHTML = (restaurant = self.restaurant) => {
  const name = document.getElementById('restaurant-name');
  name.innerHTML = restaurant.name;

  const address = document.getElementById('restaurant-address');
  address.innerHTML = restaurant.address;

  const image = document.getElementById('restaurant-img');
  image.className = 'restaurant-img';
  image.alt = restaurant.name + ' Restaurant';
  image.src = DBHelper.imageUrlForRestaurant(restaurant);

  /* add two sources with srcset and media for the picture tag */
  const sourceSmall = document.getElementById('picture-small');
  sourceSmall.srcset = DBHelper.smallImageUrlForRestaurant(restaurant);
  sourceSmall.media = '(max-width: 450px)';

  const sourceLarge = document.getElementById('picture-large');
  sourceLarge.srcset = DBHelper.imageUrlForRestaurant(restaurant);
  sourceLarge.media = '(min-width: 451px)';


  const cuisine = document.getElementById('restaurant-cuisine');
  cuisine.innerHTML = restaurant.cuisine_type;

  // fill operating hours
  if (restaurant.operating_hours) {
    fillRestaurantHoursHTML();
  }
  // fill reviews
  fetchReviewFromURL();
}

/**
 * Create restaurant operating hours HTML table and add it to the webpage.
 */
fillRestaurantHoursHTML = (operatingHours = self.restaurant.operating_hours) => {
  const hours = document.getElementById('restaurant-hours');
  for (let key in operatingHours) {
    const row = document.createElement('tr');

    const day = document.createElement('td');
    day.innerHTML = key;
    row.appendChild(day);

    const time = document.createElement('td');
    time.innerHTML = operatingHours[key];
    row.appendChild(time);

    hours.appendChild(row);
  }
}

/**
 * Like for the restaurant, we do the same for reviews
 *
 *
 * Get all reviews HTML from URL page.
 */
fetchReviewFromURL = () => {
  if (self.review) {
    return;
  }

  const id = getParameterByName('id');
  if (!id) { // no id found in URL
    error = 'No restaurant id in URL'
  } else {
    DBHelper.fetchReviewById(id, (error, reviews) => {
      self.reviews = reviews;

      if (!reviews) {
        console.error(error);
        fillReviewsHTML(null);
        return;
      }
      fillReviewsHTML();

    })
  }
}

/**
 * Create all reviews HTML and add them to the webpage.
 */
fillReviewsHTML = (reviews = self.reviews) => {
  const container = document.getElementById('reviews-container');
  const title = document.createElement('h2');
  title.innerHTML = 'Reviews';
  title.setAttribute('aria-level', '2');
  title.setAttribute('role', 'heading');
  title.setAttribute('tabindex', '0')

  container.appendChild(title);

  if (!reviews) {
    const noReviews = document.createElement('p');
    noReviews.innerHTML = 'No reviews yet!';
    container.appendChild(noReviews);
    return;
  }
  const ul = document.getElementById('reviews-list');
  reviews.forEach(review => {
    ul.appendChild(createReviewHTML(review));
  });
  container.appendChild(ul);
}

/**
 * Create review HTML and add it to the webpage.
 */
createReviewHTML = (review) => {
  const li = document.createElement('li');
  const name = document.createElement('p');
  const div = document.createElement('div');
  name.innerHTML = review.name;
  name.classList.add("reviewer");
  div.appendChild(name);

  const date = document.createElement('p');
  date.innerHTML = formatDate(review.createdAt);
  date.classList.add("review-date");
  div.appendChild(date);

  li.appendChild(div);

  const rating = document.createElement('p');
  rating.innerHTML = `Rating: ${review.rating}`;
  rating.classList.add("review-rating");
  li.appendChild(rating);

  const comments = document.createElement('p');
  comments.innerHTML = review.comments;
  li.appendChild(comments);

  return li;
}

/**
 * Add restaurant name to the breadcrumb navigation menu
 */
fillBreadcrumb = (restaurant = self.restaurant) => {
  const breadcrumb = document.getElementById('breadcrumb');
  const li = document.createElement('li');
  li.innerHTML = restaurant.name;
  breadcrumb.appendChild(li);
}

/**
 * Get a parameter by name from page URL.
 */
getParameterByName = (name, url) => {
  if (!url)
    url = window.location.href;
  name = name.replace(/[\[\]]/g, '\\$&');
  const regex = new RegExp(`[?&]${name}(=([^&#]*)|&|#|$)`),
    results = regex.exec(url);
  if (!results)
    return null;
  if (!results[2])
    return '';
  return decodeURIComponent(results[2].replace(/\+/g, ' '));
}

/**
 * Add a function to format the date in the review
 */
formatDate = (dates) => {
  const date = new Date(dates);
  return date.getDate() +'/' + (date.getMonth() + 1) + '/' + date.getFullYear();
}

/**
 * Add a service worker.
 */

if ('serviceWorker' in navigator) {
  window.addEventListener('load', function () {
    navigator.serviceWorker.register('service-worker.js', {
        scope: '/'
      })
      .then(function (registration) {
        // Registration was successful
        console.log('ServiceWorker registration successful with scope: ', registration.scope);
      }).catch(function (error) {
        console.log('ServiceWorker registration failed: error:', error);
      })
  });
}

/**
 * Gathers values from the form and submits it to backend server
 */
submitReviewForm = () => {

  const review = {};
  const formElement = document.getElementById('review-form');
  for (var i = 0; i < formElement.length; ++i) {
    const fieldName = formElement[i].name;
    const value = formElement[i].value;
    if (fieldName === "" || value === "") continue;
    if (fieldName === "restaurant_id" || fieldName === "rating") {
      value = parseInt(value);
    }
    review[formElement[i].name] = value;
  }
  formElement.reset();

  // Make the request
  postReview(review);
}

/**
 * Initialize
 */
(()=> {

    var form = document.getElementById('review-form');
    form.addEventListener('submit', function(e) {
      e.preventDefault();
      submitReviewForm();
    })



    document.addEventListener("reviews_updated", e => {
      console.log("Got reviews updated event",e);
      console.log("current restaurant",self.restaurant);
      if (e.detail.restaurant_id === self.restaurant.id) {
        console.log("Updating review list");
        DBHelper.getReviews(self.restaurant.id).then(reviews => {
          self.restaurant.reviews = reviews;
          fillReviewsHTML(reviews);
        })
      }
    });
  })
  .then(fillRestaurantHTML)
  .catch((error) => {
    console.error('Init Error: ', error);
  });
